import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "./HeadBarSearch.css";
import { Slider } from "@mui/material";
const HeadBarSearch = () => {
  const myState = useSelector(state => state.updateProps);
  const dispatch = useDispatch();

  const [max,setMax] = useState(30);

  const handleAlgo = (algo) => {
     dispatch({
        type: 'UPDATE_SEARCH_ALGO',
        search_algo: algo
     })
  }

  const resetColor = () => {
    dispatch({
       type:'UPDATE_COLOR',
       color:document.getElementById('color').value
    })
 }

 const handleRange = (_range) => {

  let new_arr = [...myState.values];
  for(let i = 0; i < new_arr.length; i++)
     document.getElementById(i).style.transform = `translateX(${i*11}px)`;

  resetColor();
  dispatch({
     type: 'UPDATE_RANGE',
     range: _range
  })
  dispatch({
     type:'CHANGE_VALUES'
  })
}

  const handleColor = (_color) => {
    dispatch({
      type: "UPDATE_COLOR",
      color: _color,
    });
  };

  const handleSpeed = (_speed) => {
    dispatch({
      type: "UPDATE_SPEED",
      speed: _speed,
    });
  };

  useEffect(() => {
    handleRange(30);
  },[]);
  
  useEffect(() => {
    dispatch({
       type:'UPDATE_COLOR',
       color:document.getElementById('color').value
    })
  },[myState.values]);

const handleWidth = () => {
  if(window.innerWidth>1300)
     setMax(70);
  else if(window.innerWidth>1200)
     setMax(60);
  else if(window.innerWidth>1100)
     setMax(50);
  else if(window.innerWidth>900)
     setMax(45);
  else if(window.innerWidth>800)
     setMax(40);
  else if(window.innerWidth>500)
     setMax(30);
  else
     setMax(20);
}

useEffect(() => {
  handleWidth();
  window.addEventListener('resize',handleWidth);
  return () => window.removeEventListener('resize',handleWidth);
},[]);

  return (
    <div className="headbar">
      <div className="headbarsearch__option">
        <label htmlFor="number">Enter a number to Search:</label>
        <input className="search_num form-control" type="text" required/>
        <label htmlFor="algo">Algorithm: </label>
        <select
          name="algo"
          id="algo"
          onChange={(e) => handleAlgo(e.target.value)}
          disabled={myState.play && !myState.pause ? true : false}>
          <option value="binary">Binary Search</option>
          <option value="linear">Linear Search</option>
         
        </select>

        <label htmlFor="range">Range: </label>
        <Slider
          style={{ width: "180px" }}
          size="small"
          id="slider"
          min={1}
          className="slider"          
          defaultValue={30}
          valueLabelDisplay="auto"
        />

        <label htmlFor="color">Color:</label>
        <select
          name="color"
          id="color"         >
          <option value="pink">Pink</option>
          <option value="blue">Blue</option>
          <option value="yellow">Yellow</option>
          <option value="green">Green</option>
        </select>

        <label htmlFor="speed">Speed: </label>
        <select
          name="speed"
          id="speed">
          <option value={500}>Slow</option>
          <option value={200}>Medium</option>
          <option value={100}>Fast</option>
          <option value={20}>Super Fast</option>
          <option value={5}>Ultra Fast</option>
        </select>

      </div>
    </div>
  );
};

export default HeadBarSearch;
